mapper.select:
SELECT id 
FROM users
WHERE id=#{id}

mapper.update:
UPDATE users
SET email=#{email }, profile=#{profile }
WHERE id=#{id }

UPDATE users
SET pwd=#{newPwd}
WHERE id=#{id}

mapper.select:
SELECT * FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT id,email,regdate,adminNum,point FROM users
WHERE id LIKE '%${idWord }%'
ORDER BY id DESC) result1) WHERE rnum BETWEEN #{startRowNum} AND ${endRowNum}

mapper.update:
UPDATE users
SET adminNum=1
WHERE id=#{id }
